sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var item = newValue;
    var qauntity = g_form.getValue('quantity') || 1;
    var ga = new GlideAjax('x_1851699_it.GetItemPrice'); 
    ga.addParam('sysparm_name', 'getPrice');
    ga.addParam('item', item);
    ga.getXML(getResponse);

	function getResponse(response) {
            var answer = response.responseXML.documentElement.getAttribute("answer");
			var total_price = answer * qauntity;
            g_form.setValue('total_price',total_price.toString());
        }

}
